import React, { useEffect, useState } from 'react';
import { Container, Typography, Grid, Card, CardContent, Avatar, Button, Box, Snackbar, Alert } from '@mui/material';
import axiosInstance from '../api/axiosConfig';

const VotePage = () => {
  const [candidates, setCandidates] = useState([]);
  const [selectedCandidate, setSelectedCandidate] = useState(null);
  const [openSnackbar, setOpenSnackbar] = useState(false);

  useEffect(() => {
    axiosInstance.get('/api/candidate')
      .then(response => setCandidates(response.data))
      .catch(error => console.error('Error fetching candidates:', error));
  }, []);

  const handleVote = () => {
    if (selectedCandidate) {
      axiosInstance.post('/api/vote', null, { params: { candidateId: selectedCandidate } })
        .then(() => setOpenSnackbar(true))
        .catch(error => console.error('Error voting:', error));
    } else {
      alert('Please select a candidate to vote for.');
    }
  };

  const handleCloseSnackbar = () => {
    setOpenSnackbar(false);
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom textAlign="center">Vote for Your Candidate</Typography>
      <Box display="flex" justifyContent="center" flexWrap="wrap" gap={2} mt={4}>
        {candidates.map(candidate => (
          <Card
            key={candidate.id}
            sx={{
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              cursor: 'pointer',
              border: selectedCandidate === candidate.id ? '2px solid #3498db' : 'none',
              padding: 2,
              width: 150
            }}
            onClick={() => setSelectedCandidate(candidate.id)}
          >
            <Avatar
              src={`data:image/jpeg;base64,${candidate.photo}`}
              alt={candidate.name}
              sx={{ width: 56, height: 56, marginBottom: 1 }}
            />
            <CardContent sx={{ textAlign: 'center' }}>
              <Typography variant="h6">{candidate.name}</Typography>
            </CardContent>
          </Card>
        ))}
      </Box>
      <Box mt={4} textAlign="center">
        <Button variant="contained" color="primary" onClick={handleVote}>Vote</Button>
      </Box>
      <Snackbar
        open={openSnackbar}
        autoHideDuration={5000}
        onClose={handleCloseSnackbar}
      >
        <Alert onClose={handleCloseSnackbar} severity="success" sx={{ width: '100%' }}>
          Vote registered successfully!
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default VotePage;
