import React, { useEffect, useState } from 'react';
import { Container, Typography, Grid, Card, CardContent, Avatar, Box } from '@mui/material';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import axiosInstance from '../api/axiosConfig';

const ResultsPage = () => {
  const [results, setResults] = useState([]);

  useEffect(() => {
    axiosInstance.get('/api/vote/results')
      .then(response => setResults(response.data))
      .catch(error => console.error('Error fetching results:', error));
  }, []);

  const topThree = results.slice(0, 3);

  return (
    <Container>
      <Typography variant="h4" gutterBottom>Election Results</Typography>
      <Grid container spacing={2}>
        {topThree.map((candidate, index) => (
          <Grid item key={candidate.id} xs={12} sm={6} md={4}>
            <Card sx={{ padding: 2 }}>
              <CardContent sx={{ display: 'flex', alignItems: 'center', flexDirection: 'column' }}>
                <Avatar
                  src={`data:image/jpeg;base64,${candidate.photo}`}
                  alt={candidate.name}
                  sx={{ width: 56, height: 56, marginBottom: 2 }}
                />
                <Typography variant="h6">{candidate.name}</Typography>
                <Typography variant="body2" color="textSecondary">Votes: {candidate.votes}</Typography>
                {index === 0 && <Typography variant="h6" color="secondary">Winner</Typography>}
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
      <Box mt={4}>
        <Typography variant="h5" gutterBottom>Top 3 Candidates Vote Distribution</Typography>
        <ResponsiveContainer width="100%" height={400}>
          <BarChart data={topThree} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="name" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey="votes" fill="#3498db" />
          </BarChart>
        </ResponsiveContainer>
      </Box>
    </Container>
  );
};

export default ResultsPage;
