import React, { useEffect, useState } from 'react';
import { Container, Typography, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Avatar, IconButton, TextField, Box, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, Button } from '@mui/material';
import { Delete as DeleteIcon } from '@mui/icons-material';
import axiosInstance from '../api/axiosConfig';

const CandidatePage = () => {
  const [candidates, setCandidates] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    photo: null,
    party: '',
    age: '',
    experience: ''
  });
  const [open, setOpen] = useState(false);

  useEffect(() => {
    fetchCandidates();
  }, []);

  const fetchCandidates = () => {
    axiosInstance.get('/api/candidate')
      .then(response => setCandidates(response.data))
      .catch(error => console.error('Error fetching candidates:', error));
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, photo: e.target.files });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append('name', formData.name);
    data.append('photo', formData.photo);
    data.append('party', formData.party);
    data.append('age', formData.age);
    data.append('experience', formData.experience);

    axiosInstance.post('/api/candidate/add', data)
      .then(() => {
        fetchCandidates(); // Refresh the candidate list
        setFormData({
          name: '',
          photo: null,
          party: '',
          age: '',
          experience: ''
        });
        setOpen(false); // Close the dialog after submission
      })
      .catch(error => console.error('Error adding candidate:', error));
  };

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <Container>
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={4}>
        <Typography variant="h4">Candidate List</Typography>
        <Button variant="contained" color="primary" onClick={handleClickOpen}>
          Add New Candidate
        </Button>
      </Box>
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Add New Candidate</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Please fill out the form below to add a new candidate.
          </DialogContentText>
          <Box component="form" onSubmit={handleSubmit} sx={{ mt: 2 }}>
            <TextField
              label="Name"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              fullWidth
              required
              sx={{ mb: 2 }}
            />
            <TextField
              type="file"
              name="photo"
              onChange={handleFileChange}
              fullWidth
              required
              sx={{ mb: 2 }}
            />
            <TextField
              label="Party"
              name="party"
              value={formData.party}
              onChange={handleInputChange}
              fullWidth
              required
              sx={{ mb: 2 }}
            />
            <TextField
              label="Age"
              name="age"
              type="number"
              value={formData.age}
              onChange={handleInputChange}
              fullWidth
              required
              sx={{ mb: 2 }}
            />
            <TextField
              label="Experience"
              name="experience"
              value={formData.experience}
              onChange={handleInputChange}
              fullWidth
              required
              sx={{ mb: 2 }}
            />
            <DialogActions>
              <Button onClick={handleClose} color="secondary">Cancel</Button>
              <Button type="submit" variant="contained" color="primary">Add Candidate</Button>
            </DialogActions>
          </Box>
        </DialogContent>
      </Dialog>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Photo</TableCell>
              <TableCell>Name</TableCell>
              <TableCell>Party</TableCell>
              <TableCell>Age</TableCell>
              <TableCell>Experience</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {candidates.map(candidate => (
              <TableRow key={candidate.id}>
                <TableCell>
                  <Avatar src={`data:image/jpeg;base64,${candidate.photo}`} alt={candidate.name} />
                </TableCell>
                <TableCell>{candidate.name}</TableCell>
                <TableCell>{candidate.party}</TableCell>
                <TableCell>{candidate.age}</TableCell>
                <TableCell>{candidate.experience}</TableCell>
                <TableCell>
                  <IconButton color="secondary" onClick={() => deleteCandidate(candidate.id)}>
                    <DeleteIcon />
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Container>
  );

  function deleteCandidate(id) {
    axiosInstance.delete(`/api/candidate/${id}`)
      .then(() => setCandidates(candidates.filter(candidate => candidate.id !== id)))
      .catch(error => console.error('Error deleting candidate:', error));
  }
};

export default CandidatePage;
