import React, { useEffect, useState } from 'react';
import { Container, Typography, Grid, Card, CardContent, CardMedia, Button } from '@mui/material';
import axiosInstance from '../api/axiosConfig';

const CandidatePage = () => {
  const [candidates, setCandidates] = useState([]);

  useEffect(() => {
    axiosInstance.get('/api/candidate')
      .then(response => setCandidates(response.data))
      .catch(error => console.error('Error fetching candidates:', error));
  }, []);

  return (
    <Container>
      <Typography variant="h4" gutterBottom>Candidate List</Typography>
      <Grid container spacing={4}>
        {candidates.map(candidate => (
          <Grid item key={candidate.id} xs={12} sm={6} md={4}>
            <Card>
              <CardMedia
                component="img"
                height="140"
                image={`data:image/jpeg;base64,${candidate.photo}`}
                alt={candidate.name}
              />
              <CardContent>
                <Typography variant="h5">{candidate.name}</Typography>
                <Typography variant="body2" color="textSecondary">{candidate.party}</Typography>
                <Typography variant="body2" color="textSecondary">Age: {candidate.age}</Typography>
                <Typography variant="body2" color="textSecondary">Experience: {candidate.experience}</Typography>
              </CardContent>
              <Button variant="contained" color="secondary" onClick={() => deleteCandidate(candidate.id)}>
                Delete
              </Button>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );

  function deleteCandidate(id) {
    axiosInstance.delete(`/api/candidate/${id}`)
      .then(() => setCandidates(candidates.filter(candidate => candidate.id !== id)))
      .catch(error => console.error('Error deleting candidate:', error));
  }
};

export default CandidatePage;
