import React from 'react';
import { Container, Typography, Box } from '@mui/material';
import voteImage from '../images/vote2.jpg'; // Adjust the path based on your folder structure

const LandingPage = () => {
  return (
    <Container
      maxWidth="lg"
      sx={{
        textAlign: 'center',
        mt: 10,
        mb: 10,
        backgroundImage: `url(${voteImage})`, // Use the imported image
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        height: '100vh', // Full height
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        color: '#fff' // Adjust text color for contrast
      }}
    >
      <Typography variant="h2" gutterBottom sx={{ fontWeight: 'bold' }}>
        Welcome to the Election App
      </Typography>
      <Typography variant="h5" gutterBottom sx={{ lineHeight: 1.6 }}>
        Your one-stop platform for managing election candidates and their details.
      </Typography>
      <Box mt={5} sx={{ color: '#7f8c8d' }}>
        <Typography variant="body2">
          &copy; {new Date().getFullYear()} Election App. All rights reserved.
        </Typography>
      </Box>
    </Container>
  );
};

export default LandingPage;
