import React, { useEffect, useState } from 'react';
import { Container, Typography, Grid, Card, CardContent, Avatar, Button, Box } from '@mui/material';
import axiosInstance from '../api/axiosConfig';

const VotePage = () => {
  const [candidates, setCandidates] = useState([]);
  const [selectedCandidate, setSelectedCandidate] = useState(null);

  useEffect(() => {
    axiosInstance.get('/api/candidate')
      .then(response => setCandidates(response.data))
      .catch(error => console.error('Error fetching candidates:', error));
  }, []);

  const handleVote = () => {
    if (selectedCandidate) {
      axiosInstance.post('/api/vote', null, { params: { candidateId: selectedCandidate } })
        .then(() => alert('Vote registered successfully'))
        .catch(error => console.error('Error voting:', error));
    } else {
      alert('Please select a candidate to vote for.');
    }
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>Vote for Your Candidate</Typography>
      <Grid container spacing={4}>
        {candidates.map(candidate => (
          <Grid item key={candidate.id} xs={12} sm={6} md={4}>
            <Card
              sx={{
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                cursor: 'pointer',
                border: selectedCandidate === candidate.id ? '2px solid #3498db' : 'none'
              }}
              onClick={() => setSelectedCandidate(candidate.id)}
            >
              <Avatar
                src={`data:image/jpeg;base64,${candidate.photo}`}
                alt={candidate.name}
                sx={{ width: 80, height: 80, marginBottom: 2 }}
              />
              <CardContent>
                <Typography variant="h5">{candidate.name}</Typography>
                <Typography variant="body2" color="textSecondary">{candidate.party}</Typography>
                <Typography variant="body2" color="textSecondary">Age: {candidate.age}</Typography>
                <Typography variant="body2" color="textSecondary">Experience: {candidate.experience}</Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
      <Box mt={4} textAlign="center">
        <Button variant="contained" color="primary" onClick={handleVote}>Vote</Button>
      </Box>
    </Container>
  );
};

export default VotePage;
